export const BEAST_CATEGORIES: CompanionOption[] = [
    { id: 'humanoid', title: 'HUMANOID', cost: 0, imageSrc: 'https://cdn.jsdelivr.net/gh/theapple1234/saviapple_Seinarumagecraft_CYOA/public/images/354FrQRd-humanoid.webp', description: "Broadly applies to anything bipedal or at least vaguely hominid-esque in stature. You can make creatures that look very human, but never exactly so. It will always be immediately obvious to everyone who sees them, in some way or another, that your beast is not truly part of mankind." },
    { id: 'pawed', title: 'PAWED', cost: 0, imageSrc: 'https://cdn.jsdelivr.net/gh/theapple1234/saviapple_Seinarumagecraft_CYOA/public/images/8nVM7whK-pawed.webp', description: "This covers pretty much all mammals that have paws, like cats, dogs, otters, and what have you. Compared to ungulates, they tend to be pretty thickset and sturdy and have shorter fur. Great for hugs! These are the most popular type of beast, as they make for excellent pets and companions." },
    { id: 'ungulate', title: 'UNGULATE', cost: 0, imageSrc: 'https://cdn.jsdelivr.net/gh/theapple1234/saviapple_Seinarumagecraft_CYOA/public/images/vxRZgc77-ungulate.webp', description: "This covers all the mammals that have hooves, from horses, to elephants, to rhinoceroses, and all that. Compared to pawed mammals, they tend to be pretty thickset and sturdy and have shorter fur. Great as work animals! They are most popular as mounts. Mages particularly love unicorns." },
    { id: 'avian', title: 'AVIAN', cost: 0, imageSrc: 'https://cdn.jsdelivr.net/gh/theapple1234/saviapple_Seinarumagecraft_CYOA/public/images/F4SRndPr-avian.webp', description: "Pretty popular as they're associated with flying. Of course, not all avians are actually capable of flight. Just ask the humble penguin. Signified by their feathers and pokey beaks. They're fairly popular as pets and familiars, but not as much as mammals are." },
    { id: 'reptilian', title: 'REPTILIAN', cost: 0, imageSrc: 'https://cdn.jsdelivr.net/gh/theapple1234/saviapple_Seinarumagecraft_CYOA/public/images/8nVd4J22-reptilian.webp', description: "Not too popular among most, since their scaley bodies and angular features tend to make them unappealing as pets. But some find them absolutely adorable in their own way, and some mages covet them for their association with strength and power." },
    { id: 'aquatic', title: 'AQUATIC', cost: 0, imageSrc: 'https://cdn.jsdelivr.net/gh/theapple1234/saviapple_Seinarumagecraft_CYOA/public/images/B5dwQkqG-aquatic.webp', description: "These sorts of pets aren't very popular in most places, but their immense popularity in the Unterseeisch tends to make up for that. They can survive on land just fine, too. In fact, this category also covers amphibians." },
    { id: 'insectoid', title: 'INSECTOID', cost: 0, imageSrc: 'https://cdn.jsdelivr.net/gh/theapple1234/saviapple_Seinarumagecraft_CYOA/public/images/fGTNHfvK-insectoid.webp', description: "Probably the least popular pets and familiars, especially if we're talking anything besides butterflies and the occasional moth. They just creep most people out! But they're good for mages who like scaring and intimidating others, and again, some find them weirdly cute." },
    { id: 'abstract', title: 'ABSTRACT', cost: 0, imageSrc: 'https://cdn.jsdelivr.net/gh/theapple1234/saviapple_Seinarumagecraft_CYOA/public/images/mCwvxzJY-abstract.webp', description: "This encompasses perhaps the broadest range of creatures, since they are no longer required to even look remotely like a real animal. They can be formed in ways that defy all biological sense, and are utterly unique to the mage who owns them." },
];

